yer.style.display = \"none\";\n    	}\n            \n            function cargando_{*nombre*}() {\n    		if (browserType == \"gecko\" )\n    			document.poppedLayer = eval(\'document.getElementById(\"esperando_{*nombre*}\")\');\n    		else if (browserType == \"ie\")\n    			document.poppedLayer = eval(\'document.getElementById(\"esperando_{*nombre*}\")\');\n    		else\n    			document.poppedLayer =	eval(\'document.layers[\"esperando_{*nombre*}\"]\');\n    			document.poppedLayer.style.display = \"\";\n    	}\n      function onNodeSelect_{*nombre*}(nodeId){\n        valor_destino=document.getElementById(\"{*nombre*}\");\n				\n					destinos=tree_{*nombre*}.getAllChecked();\n					var nuevos_valores=destinos.split(\",\");\n					var cantidad=nuevos_valores.length;\n					var funcionarios=new Array();\n					var indice=0;\n					for(var i=0;i<cantidad;i++){\n						if(nuevos_valores[i].indexOf(\"#\")==\"-1\"){\n							if(nuevos_valores[i]!=\"\"){\n								funcionarios[indice]=nuevos_valores[i];\n								indice++;\n							}\n						}\n					}\n					valor_destino.value=funcionarios.join(\",\");\n				\n      }\n    	{*caracteristicas_arbol_checkbox*}		\n    });\n    </script>		\n  </div>\n</div>', '{\"nombre\":\"arbol_checkbox\",\"etiqueta\":\"Arboles \",\"tipo_dato\":\"varchar\",\"longitud\":\"255\",\"obligatoriedad\":1,\"valor\":\"../../pantallas/lib/arbol_funcionarios.php;1;0;1;1;0;0\",\"acciones\":\"a,e,b\",\"ayuda\":\"\",\"predeterminado\":\"\",\"banderas\":\"\",\"etiqueta_html\":\"arbol\",\"orden\":1,\"mascara\":\"\",\"adicionales\":\"\",\"autoguardado\":1,\"fila_visible\":1}', 'mostrar_arbol_checkbox', 'Arbol', 0, 'js/dhtmlXTree.js,js/dhtmlXCommon.js,css/dhtmlXTree.css,pantallas/lib/librerias_codificacion.js,pantallas/lib/librerias_arboles.js,css/bootstrap/saia/css/bootstrap_reescribir.css', 1, '', 'arbol', '{\n  \"schema\": {\n    \"fs_etiqueta\": {\n      \"type\": \"string\",\n      \"title\": \"Etiqueta del campo\",\n      \"maxLength\": 255,\n      \"required\": true\